    Dim result As String = String.Empty
    Dim i As Integer

    For i = 0 To words.Length - 1
      s = words(i)
      If <span class="code-changed">s.Length &lt;&gt; 0</span> Then
        If <span class="code-changed">result.Length = 0</span> Then
          result = s
        Else
          result &= delimeter & s
        End If
      End If
    Next i

    Return result

  End Function

