        If a < 4 Then
          ' some code
        End If
      End Sub

      Public Sub Foo_Switch()
        Dim a As Integer = 0
        Select Case a
          Case 3
          ' some code
        End Select
      End Sub

      Public Sub Foo_Else()
        Dim a As Integer = 0
        If a > 5 Then
          ' some code
        Else
          ' some code
        End If
      End Sub

      Public Sub Foo_For()
        For value As Integer = 0 To 5
          ' some code
        Next
      End Sub

      Public Sub Foo_While()
        Dim a As Integer = 0
        While a < 5
          a += 1
        End While
      End Sub

      Public Sub Foo_Do()
        Dim a As Integer = 0
        Do
          a += 1
        Loop While a < 5
