    public class SomeClass
    {
      public void Foo_If()
      {
        int a = 0;
        if (a < 4)
        {
          // some code
        }
      }

      public void Foo_Switch()
      {
        int a = 0;
        switch (a)
        {
          case 3:
            {
              // some code
              break;
            }
        }
      }

      public void Foo_Else()
      {
        int a = 0;
        if (a > 5)
        {
          // some code
        }
        else
        {
          // some code
        }
      }

      public void Foo_For()
      {
        for (; ; )
        {
          // some code
        }
      }

      public void Foo_While()
      {
        int a = 0;
        while (a < 5)
        {
          a++;
        }
      }

      public void Foo_Do()
      {
        int a = 0;
        do
        {
          a++;
        }
        while (a < 5);
      }
