        &lt;OutArgument x:TypeArguments="x:Int32"&gt;
          &lt;ArgumentReference x:TypeArguments="x:Int32" ArgumentName="Result" /&gt;
        &lt;/OutArgument&gt;
      &lt;/Assign.To&gt;
    &lt;/Assign&gt;
  &lt;/Sequence&gt;
&lt;/Activity&gt;

	</pre>
  <p>&nbsp;
